var hash = exports;

hash.utils = require('hash.js/lib/hash/utils');
hash.common = require('hash.js/lib/hash/common');
hash.sha = require('hash.js/lib/hash/sha');
hash.ripemd = require('hash.js/lib/hash/ripemd');
hash.hmac = require('hash.js/lib/hash/hmac');

// Proxy hash functions to the main object
hash.sha1 = hash.sha.sha1;
hash.sha256 = hash.sha.sha256;
hash.sha224 = hash.sha.sha224;
hash.sha384 = hash.sha.sha384;
hash.sha512 = hash.sha.sha512;
hash.ripemd160 = hash.ripemd.ripemd160;
